mapper.select:
select reserve_no
from reserve
where reserve_in = TO_TIMESTAMP(#{reserve_in}, 'YYYY-MM-DD HH24:MI:SS.FF') 
and reserve_out = TO_TIMESTAMP(#{reserve_out}, 'YYYY-MM-DD HH24:MI:SS.FF') 
and room_no = #{room_no}
and mem_id = #{mem_id}
and dog_no = #{dog_no}
and reserve_cancel = 'N'

select reserve_no
from reserve
where reserve_in = TO_TIMESTAMP(#{reserve_in}, 'YYYY-MM-DD HH24:MI:SS.FF') 
and reserve_out = TO_TIMESTAMP(#{reserve_out}, 'YYYY-MM-DD HH24:MI:SS.FF') 
and salon_no = #{salon_no}
and mem_id = #{mem_id}
and dog_no = #{dog_no}
and reserve_cancel = 'N'

mapper.update:
update reserve
set reserve_cancel = 'Y'
where reserve_no = #{reserve_no}
