mapper.update:
update reply
   set reply_content = #{reply_content}
   ,   reply_date = sysdate  
 where reply_no = #{reply_no}
   and board_no = #{board_no}

update reply
   set reply_delyn = 'Y'
 where reply_no = #{reply_no}
   and board_no = #{board_no}

mapper.select:
select * from reply 
 where board_no = #{board_no}
   and reply_delyn = 'N'
